(412, 1, 'ECR International Ltd. ', 'info@ecrinternational.com', 'P.O. Box 900, Main Stn, WALLACEBURG, Ontario, , ', 'www.ecrinternational.com', '+1 (519) 627-0791', '+1 (519) 627-4719', '', '<p>\r\n\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\">\r\n\n<meta name=\"ProgId\" content=\"PowerPoint.Slide\">\r\n\n<meta name=\"Generator\" content=\"Microsoft PowerPoint 12\"> </meta>\r\n\n</meta>\r\n\n</meta>\r\n\n</p>\r\n\n<p style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; text-align: justify; direction: ltr; unicode-bidi: embed;\"><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\">ECR has made it possible for people around the world to enjoy a higher standard of indoor heating and cooling comfort.</span></p>\r\n\n<p style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; text-align: justify; direction: ltr; unicode-bidi: embed;\"><span style=\"font-size: 8pt; font-family: &quot;Century Gothic&quot;; color: black;\"> </span></p>\r\n\n<p style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; text-align: justify; direction: ltr; unicode-bidi: embed;\"><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\">As a full-service provider of American engineered-and-manufactured </span><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\">hydronic</span><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\"> and forced air products, ECR is recognized worldwide for its innovation, quality, performance and reliability.</span></p>\r\n\n<p style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; text-align: justify; direction: ltr; unicode-bidi: embed;\"><span style=\"font-size: 8pt; font-family: &quot;Century Gothic&quot;; color: black;\"> </span></p>\r\n\n<p style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; text-align: justify; direction: ltr; unicode-bidi: embed;\"><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\">ECR designs, manufactures and markets HVAC equipment including boilers, water heaters, </span><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\">hydronic</span><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\"> system controls and furnaces in modern plants at Dunkirk and Utica New York; and </span><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\">Wallaceburg</span><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\">, Ontario, Canada. A fourth </span><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\">ECR facility in Rome, New York, is dedicated to the design and manufacture of cooling products for residential, light commercial, institutional, and hospitality markets.</span><span style=\"font-size: 8pt; font-family: &quot;Century Gothic&quot;; color: black;\"> </span></p>', ''),
(413, 1, 'Clemmer SteelCraft Technologies Inc. ', 'kzehr@clemmersteelcraft.com', 'P.O. Box 339, STRATFORD, Ontario, , ', 'www.clemmersteelcraft.com', '+1 (519) 271-4750', '+1 (310) 272-0911 ', '', '<p>\r\n\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\">\r\n\n<meta name=\"ProgId\" content=\"PowerPoint.Slide\">\r\n\n<meta name=\"Generator\" content=\"Microsoft PowerPoint 12\"> </meta>\r\n\n</meta>\r\n\n</meta>\r\n\n</p>\r\n\n<p style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; text-align: justify; direction: ltr; unicode-bidi: embed;\"><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\">SteelCraft</span><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\"> Industries Limited is a Canadian company incorporated in Ontario in 1972. Its offices and 130,000 square feet of manufacturing area are located in Stratford, in Mid-Western Ontario approximately 120 km from Toronto and 250km from Detroit, Michigan, with another 70,000 square foot facility in Woodstock, 45 km South of Stratford. The company consists of four sales divisions each with its own product line. These are Quality Components Inc., Festival City Fabricators Inc., Bowers Process Equipment Inc., and </span><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\">FerroForm</span><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\"> Inc.</span></p>\r\n\n<p style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; text-align: justify; direction: ltr; unicode-bidi: embed;\"><span style=\"font-size: 4pt; font-family: &quot;Century Gothic&quot;; color: black;\"> </span></p>\r\n\n<p style=\"margin-top: 0pt; margin-bottom: 0pt; margin-left: 0in; text-align: justify; direction: ltr; unicode-bidi: embed;\"><span style=\"font-size: 10pt; font-family: &quot;Century Gothic&quot;; color: black;\">SteelCraft</span><span style=\"font-size: 10pt; font-